import {StyleSheet} from 'react-native';

const styles = StyleSheet.create({
    container:{
        flex: 1
    },
    titulo:{
        fontSize: 40,
        fontFamily: 'Verdana',
        textAlign: 'center'
    },
    caixa:{
        width: 400,
        height: 250,
        padding: 10,
        marginTop: 20,
        borderWidth: 1,
        borderColor: '#222'
    },
    tituloVaga:{
        fontSize: 18,
        fontFamily: 'Verdana',
        fontWeight: 'bold',
        alignSelf: 'center'
    },
    texto:{
        fontSize: 14,
        fontFamily: 'Verdana',
    }
})

export {styles};
