import {StyleSheet} from 'react-native';

const styles = StyleSheet.create({
    container:{
        flex: 1
    },
    titulo:{
        fontSize: 40,
        fontFamily: 'Verdana',
        textAlign: 'center'
    },
    caixa:{
        width: 400,
        height: 250,
        marginTop: 20,
        borderWidth: 1,
        borderColor: '#222',
        alignSelf: 'center'
    },
    tituloVaga:{
        fontSize: 18,
        fontFamily: 'Verdana',
        fontWeight: 'bold',
        paddingTop: 10,
        paddingLeft: 5 
    },
    texto:{
        fontSize: 14,
        fontFamily: 'Verdana',
        paddingTop: 15,
        paddingLeft: 3
    }
})

export {styles};
